def add_card_to_list(card_name):
    new_card_parameters = {'key': os.environ.get('API_KEY'),'token': os.environ.get('TOKEN'), 'name': card_name, 'idList': get_starting_list_id(return_lists_on_a_board())}
    requests.post('https://api.trello.com/1/cards/', params = new_card_parameters)


